import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Form, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import Loader from '../components/Loader';
import FormContainer from '../components/FormContainer';
import { sendToken, verifyToken } from '../actions/userActions';
import { getUserCart } from '../actions/cartActions';

const TokenAuthScreen = ({ location, history }) => {
  const [token, setToken] = useState('');

  const redirect = location.search ? location.search.split('=')[1] : '/';

  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { loading, userInfo, error } = userLogin;

  const userVerifyToken = useSelector((state) => state.userVerifyToken);
  const { loading: tokenLoading, error: tokenError } = userVerifyToken;

  useEffect(() => {}, [history, tokenLoading, redirect, dispatch]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(verifyToken(userInfo.email, token, userInfo._id));
  };

  return (
    <>
      {error && <Message variant="danger">{error} </Message>}
      {loading || tokenLoading ? (
        <Loader />
      ) : (
        <FormContainer>
          <h1>Authenticate Token</h1>
          <Form onSubmit={submitHandler}>
            <Form.Group controlId="email">
              <Form.Label>Token</Form.Label>
              <Form.Control
                type="token"
                placeholder="Token"
                value={token}
                onChange={(e) => setToken(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Button type="submit" variant="primary">
              Verify Token
            </Button>
          </Form>
          <Row className="py-3">
            <Col>
              get Token Again ?{' '}
              <Link
                to={redirect ? `/register?redirect=${redirect}` : '/register'}
              >
                Register
              </Link>
            </Col>
          </Row>
        </FormContainer>
      )}
    </>
  );
};

export default TokenAuthScreen;
