import React from 'react';
import { Doughnut } from 'react-chartjs-2';

const dataArray = [12, 19, 3, 5, 2, 3];

const data = {
  labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
  datasets: [
    {
      label: '# of Votes',
      data: dataArray,
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)',
        'rgba(75, 192, 192, 0.2)',
        'rgba(153, 102, 255, 0.2)',
        'rgba(255, 159, 64, 0.2)',
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
        'rgba(255, 159, 64, 1)',
      ],
      borderWidth: 1,
    },
  ],
};

const options = {
  responsive: false,
  maintainAspectRatio: false,
};

const DoughnutChart = () => {
  return (
    <>
      <div className="header">
        {/* <h1 className="title">Doughnut Chart</h1> */}
        <div className="links">
          {/* <a
            className="btn btn-gh"
            href="https://github.com/reactchartjs/react-chartjs-2/blob/master/example/src/charts/Doughnut.js"
          >
            Github Source
          </a> */}
        </div>
      </div>
      <Doughnut data={data} options={options} width={400} height={400} />
    </>
  );
};

export default DoughnutChart;
